import React, { useContext }  from 'react'
import {EmployeesContext} from '../context/EmployeesContext'

const DeletedEmployeesList = () => {
    const {deletedEmployees} = useContext(EmployeesContext)
    return (
        <main>
            {/* <section className='list-container'>
                <h1>Deleted employees</h1>
                <article>
                    {
                        deletedEmployees.length === 0 ? <p>Nothing here...</p> :  deletedEmployees.map(deletedEmployee =>{
                            const {id, name, surname, dateOfBirth, position, phoneNumber, inputId} = deletedEmployee
                            return (
                                <div key={inputId} className="employee">
                                    <h2>{name} {surname}</h2>
                                    <h3>{position}</h3>
                                    <div>
                                        <p><span>ID:</span> {id}</p>
                                        <p><span>Date of birth:</span> {dateOfBirth}</p>
                                        <p><span>Phone number:</span> {phoneNumber}</p>
                                    </div>
                                </div>
                            )
                        })
                    }
                </article>
            </section> */}

<section className="container">
        <Search searchTerm={searchTerm} handleChange={handleChange} handleSubmit={handleSubmit}/>
        <div className="btns-container">    
            <button type='button' onClick={showModal}>Submit data</button>           
            <button type='button' onClick={resetData}>Reset data</button>        
        </div>

            <article className="table-container">
                <table id="employees">
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Surname</th>
                        <th>Date of birth</th>
                        <th>Position</th>
                        <th>Phone number</th>
                        <th>Status</th>
                        <th>Action</th>
                    </tr>
                    </thead>
                    <tbody>
                    {
                            currentEmployees.map((employee) => 
                            {
                                const {id, name, surname, dateOfBirth, position, phoneNumber, isDeleted, inputId} = employee
                                // console.log(id, name, surname, dateOfBirth, position, phoneNumber, isDeletedÃ§ inputId)
                                return(
                                <tr key={inputId} className={`status ${isDeleted ? 'deleted' : 'not-deleted'}`}>
                                    <EditableTableCell handleEmployeeTableCell={handleEmployeeTableCell} cellAttrs={{
                                    "type": "text",
                                    "name": "id",
                                    value: id,
                                    id: `${inputId}input1`
                                    }}/>
                                    <EditableTableCell handleEmployeeTableCell={handleEmployeeTableCell} cellAttrs={{
                                    "type": "text",
                                    "name": "name",
                                    value: name,
                                    id: `${inputId}input2`
                                    }}/>
                                    <EditableTableCell handleEmployeeTableCell={handleEmployeeTableCell} cellAttrs={{
                                    "type": "text",
                                    "name": "surname",
                                    value: surname,
                                    id: `${inputId}input3`
                                    }}/>
                                    <EditableTableCell handleEmployeeTableCell={handleEmployeeTableCell} cellAttrs={{
                                    "type": "date",
                                    "name": "dateOfBirth",
                                    value: dateOfBirth,
                                    id: `${inputId}input4`
                                    }}/>
                                    <EditableTableCell handleEmployeeTableCell={handleEmployeeTableCell} cellAttrs={{
                                    "type": "text",
                                    "name": "position",
                                    value: position,
                                    id: `${inputId}input5`
                                    }}/>
                                    <EditableTableCell handleEmployeeTableCell={handleEmployeeTableCell} cellAttrs={{
                                    "type": "tel",
                                    "name": "phoneNumber",
                                    value: phoneNumber,
                                    id: `${inputId}input6`
                                    }}/>
                                    <td className={`status ${isDeleted ? 'deleted' : 'not-deleted'}`}><span>{isDeleted ? 'deleted' : '.............'}</span></td>
                                    <td>
                                    {
                                        isDeleted ? <i className="fas fa-trash-restore" onClick={()=>deleteEmployee(inputId)}></i> : <i className="fas fa-trash" onClick={()=>deleteEmployee(inputId)}/>
                                    }

                                    </td>

                                </tr>
                                )
                            }
                            )
                        }
                    </tbody>
                </table>


            </article>
            {
                    isSearched ? null :<Pagination employeesPerPage={employeesPerPage} employees={employees} currentPage={currentPage} paginate={paginate}/> 
                }


        </section>
        </main>
    )
}

export default DeletedEmployeesList
